vec4 Effect2(vec2 uv) {
    // Animate UV coordinates over time
    vec2 st = uv * 5.0 + vec2(iTime, iTime*0.5);

    // Create moving sine patterns
    float pattern1 = sin(st.x + iTime) * 0.5 + 0.5;
    float pattern2 = sin(st.y + iTime*0.7 + 3.14159) * 0.5 + 0.5;

    // Mix patterns with noise
    vec2 noiseSt = uv * 10.0 + vec2(iTime, iTime);
    float noiseVal = noise(noiseSt);

    // Create RGB transitions using HSL
    vec3 colorHSL = vec3(0.5 + sin(iTime)*0.4, 0.7, 0.8);
    vec3 colorRGB = hsl2rgb(colorHSL);

    // Combine patterns and noise with color
    float finalPattern = pattern1 * pattern2 + noiseVal * 0.3;
    vec3 finalColor = mix(colorRGB, vec3(finalPattern), 0.5);

    // Add gamma correction for better contrast
    finalColor = pow(finalColor, vec3(0.7));

    return vec4(finalColor, 1.0);
}
