vec4 Effect2(vec2 uv) {
    // Rotate UV coordinates over time
    float t = iTime * 0.5;
    vec2 rotatedUV = vec2(
        uv.x * cos(t) - uv.y * sin(t),
        uv.x * sin(t) + uv.y * cos(t)
    );

    // Create polar coordinates
    vec2 polar = vec2(length(rotatedUV), atan2(rotatedUV.y, rotatedUV.x));
    float angle = polar.y + t;

    // Generate noise patterns
    float noise1 = noise(vec3(polar.x * 5.0, angle * 5.0, t));
    float noise2 = turbulent(vec2(angle * 2.0, polar.x * 2.0));

    // Create color variations
    vec3 color1 = hsl2rgb(vec3((angle + t) * 0.5, 0.7, 0.6));
    vec3 color2 = hsl2rgb(vec3((polar.x + t) * 0.5, 0.8, 0.7));

    // Combine patterns and colors
    vec3 finalColor = mix(color1, color2, noise1);
    finalColor *= sin(polar.x * 4.0 + t) * 0.5 + 0.5;
    finalColor += noise2 * 0.3;

    return vec4(finalColor, 1.0);
}
