vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    vec2 pos = uv * 2.0 - 1.0;
    float r = length(pos);
    float theta = atan2(pos.y, pos.x) + iTime * 0.5;

    // Create rotating sine waves with different frequencies for each color channel
    float red = sin(theta * 4.0 + iTime) * 0.5 + 0.5;
    float green = sin(theta * 2.0 + iTime * 0.7 + PI) * 0.5 + 0.5;
    float blue = sin(theta + iTime * 1.3 + PI*2.0) * 0.5 + 0.5;

    // Add some turbulence to create dynamic movement
    vec3 noisePos = vec3(uv * 10.0, iTime * 0.1);
    float turb = turbulent(noisePos) * 0.5 + 0.5;

    // Combine the sine waves with turbulence and color shifts
    vec3 color = vec3(
        red * (1.0 - turb) + blue * turb,
        green * (1.0 - turb) + red * turb,
        blue * (1.0 - turb) + green * turb
    );

    // Convert to RGB and scale by distance from center
    color = hsl2rgb(vec3(color.r, color.g, color.b)) * (1.0 - r * 0.5);

    return vec4(color, 1.0);
}
