vec4 Effect2(vec2 uv) {
    // Create expanding square pattern
    vec2 st = uv * 2.0 - 1.0;
    float d = length(abs(st.x) + abs(st.y));

    // Add pulsating effect using time
    float t = iTime * 0.5;
    float pulse = mod(t, PI);
    float dist = d + sin(pulse) * 0.5;

    // Create color transitions
    vec3 color1 = hsl2rgb(vec3(0.0, 1.0, 0.5));
    vec3 color2 = hsl2rgb(vec3(0.666, 1.0, 0.5));
    vec3 color3 = hsl2rgb(vec3(0.333, 1.0, 0.5));

    // Mix colors based on distance and time
    float mixAmount = (sin(t) + 1.0) * 0.5;
    vec3 finalColor = mix(color1, color2, mixAmount);
    finalColor = mix(finalColor, color3, noise(vec3(uv * 4.0, t)) * 0.5);

    // Add turbulence effect
    float turb = turbulent(vec3(uv * 2.0, t));
    finalColor *= (1.0 + sin(turb * PI) * 0.5);

    return vec4(finalColor, 1.0);
}
