vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    float a = atan2(uv.y, uv.x);
    float r = length(uv);

    // Create swirling pattern using sine waves and time
    a += sin(a * 5.0 + iTime) * 0.2;
    a += iTime * 0.1; // Add time-based rotation

    // Generate color transitions based on angle and radius
    vec3 color = hsl(
        sin(iTime * 0.5 + a * 4.0) * 0.5 + 0.5, // Hue variation over time
        0.7, // Saturation
        0.5 // Lightness
    );

    // Add turbulence for more dynamic patterns
    vec3 noisePattern = vec3(turbulent(vec3(uv * 10.0, iTime)));
    color = mix(color, noisePattern, 0.2);

    return vec4(color, 1.0);
}
