vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar coordinates
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float theta = atan2(st.y, st.x);
    // Add time-based rotation
    theta += iTime * 0.5;
    // Create a pattern using sine waves and noise
    float pattern = sin(r * 8.0 + theta) * 0.5 + 0.5;
    pattern *= turbulent(vec2(theta, r)) * 0.5 + 0.5;
    // Generate pulsating colors using HSL
    vec3 color = hsl2rgb(vec3(
        (theta * 0.5 + iTime) * 180.0 / PI,
        1.0,
        0.5
    ));
    // Combine pattern with color and noise
    vec3 finalColor = mix(color, vec3(0.0), pattern);
    return vec4(finalColor, 1.0);
}
