vec4 Effect2(vec2 uv) {
    // Create rotating sine wave pattern
    vec2 st = uv * 5.0; // Scale coordinates for more detail
    float time = iTime * 0.5; // Slow down the animation

    // Convert to polar coordinates
    vec2 polar = vec2(
        length(st - 0.5), 
        atan2(st.y - 0.5, st.x - 0.5)
    );

    // Create rotating sine wave pattern with color shifts
    float pattern = sin(polar.x * 10.0 + time) * cos(polar.y + time);
    pattern += sin(polar.x * 5.0 + time * 2.0) * sin(polar.y * 3.0 + time);

    // Add turbulence for more dynamic effect
    vec2 noiseUV = uv * 100.0;
    float turb = turbulent(noiseUV);
    pattern += turb * 0.5;

    // Create RGB color shifts using HSL to RGB conversion
    vec3 hslColor = vec3(
        fract(time * 0.2 + uv.x) * 360.0, 
        1.0, 
        0.5 + turb * 0.5
    );
    vec3 rgbColor = hsl2rgb(hslColor);

    // Combine pattern with color shifts
    vec3 finalColor = mix(
        rgbColor,
        vec3(pattern),
        abs(sin(time)) * 0.5 + 0.5
    );

    return vec4(finalColor, 1.0);
}
