vec4 Effect2(vec2 uv) {
    // Create base frequency components
    float time = iTime * 0.5;
    vec3 freqs = vec3(1.0, 1.5, 2.0);

    // Generate sine wave patterns with noise modulation
    float wave1 = sin(uv.x * freqs.x + time) * 
                  sin(uv.y * freqs.x + time * 0.7) *
                  noise(vec3(uv * 4.0, time));

    float wave2 = sin(uv.x * freqs.y + time * 0.9) *
                  sin(uv.y * freqs.y + time * 0.5) *
                  noise(vec3(uv * 6.0, time)); 

    // Combine waves with turbulence
    float combined = (wave1 + wave2) * 0.5;
    combined += turbulent(vec3(uv * 8.0, time)) * 0.4;

    // Create rotating RGB gradient
    vec3 color = hsl(
        mod(combined * 0.5 + time, 1.0),  // Rotating hue
        0.7,                               // Saturation
        0.5 + noise(vec2(uv * 4.0)) * 0.3 // Lightness variation
    );

    // Add noise texture overlay
    vec4 noiseTex = texture(iChannel0, uv * 8.0);
    color = mix(color, noiseTex.rgb, 0.3);

    return vec4(color, 1.0);
}
