vec4 Effect2(vec2 uv) {
    // Create rotating pattern using polar coordinates
    float angle = atan2(uv.y, uv.x) + iTime * 0.5;
    float radius = length(uv);

    // Generate color shifts using HSL colors
    vec3 color1 = hsl(angle * 0.1 + iTime * 0.2, 0.6, 0.7);
    vec3 color2 = hsl(angle * 0.15 - iTime * 0.3, 0.5, 0.8);

    // Add noise and turbulence for dynamic effect
    float noise1 = turbulent(vec2(uv.x * 4.0 + iTime, uv.y * 4.0));
    float noise2 = turbulent(vec3(uv * 6.0 + vec2(iTime), iTime * 0.5));

    // Combine colors and noise
    vec3 finalColor = mix(color1, color2, abs(noise1 * 0.5 + 0.5)) * (0.8 + noise2 * 0.2);

    // Add radial glow effect
    float glow = smoothstep(0.7, 0.9, radius) * (1.0 - length(uv));
    finalColor += vec3(glow * 0.4);

    return vec4(hsl2rgb(finalColor), max(glow, 0.2));
}
