vec4 Effect2(vec2 uv) {
    // Create smooth undulating waves using sine functions combined with time
    float wave1 = sin(uv.x * 20.0 + iTime * 2.0);
    float wave2 = sin((uv.y - 0.5) * 10.0 + iTime * 0.5);

    // Add turbulence for more dynamic movement
    vec3 noisePos = vec3(uv * 10.0, iTime * 0.1);
    float turb = turbulent(noisePos);

    // Combine waves and turbulence with color transitions
    float v = sin(wave1 + wave2 + turb) * 0.5 + 0.5;

    // Create vibrant color transitions using HSL
    vec3 hslColor = vec3(v * 360.0, 0.7, 0.8);
    vec3 rgbColor = hsl2rgb(hslColor);

    return vec4(rgbColor, 1.0);
}
