vec4 Effect2(vec2 uv) {
    // Create rotating circles pattern
    float radius = length(uv - 0.5);

    // Add rotation over time
    float angle = iTime * 0.1;
    vec2 rotatedUV = uv - 0.5;
    rotatedUV.x *= cos(angle);
    rotatedUV.y *= sin(angle);
    rotatedUV += 0.5;

    // Create multiple layers of circles with different frequencies
    float d1 = mod(length(rotatedUV * 4.0 - 0.5) * 2.0, 1.0);
    float d2 = mod(length(rotatedUV * 8.0 - 0.5) * 3.0, 1.0);

    // Add noise for dynamic variation
    float noiseValue = turbulent(uv * 4.0 + iTime) * 0.5 + 0.5;

    // Create color shifts using HSL to RGB conversion
    vec3 color1 = hsl2rgb(vec3(0.5 + d1 * 0.5, 1.0, 0.8));
    vec3 color2 = hsl2rgb(vec3(0.5 + d2 * 0.5, 1.0, 0.6));

    // Combine layers with varying opacity
    vec3 finalColor = mix(color1, color2, noiseValue);

    return vec4(finalColor, noiseValue);
}
