vec4 Effect2(vec2 uv) {
    vec3 color = vec3(0.0);

    // Time-based offset for animation
    float timeOffset = iTime * 0.5;

    // Create a displacement using noise and turbulence
    vec2 st = uv * 10.0 + vec2(timeOffset, timeOffset);
    float n1 = noise(st) * 0.5;
    float n2 = turbulent(st * 0.5) * 0.3;
    float n3 = turbulent(st * 0.25) * 0.2;

    // Combine noise layers
    float displacement = (n1 + n2 + n3) * 0.5 + 0.5;

    // Modulate color based on displacement and time
    vec3 baseColor = hsl2rgb(vec3((timeOffset * 0.1) + displacement, 1.0, 0.6));

    // Add a radial glow effect
    float distFromCenter = length(uv - 0.5);
    float glowFactor = smoothstep(0.7, 0.0, distFromCenter);
    vec3 glowColor = hsl2rgb(vec3((timeOffset * 0.1), 1.0, 0.8)) * glowFactor;

    // Combine colors
    color = mix(baseColor, glowColor, glowFactor);

    return vec4(color, 1.0);
}
