vec4 Effect2(vec2 uv) {
    // Create a checkerboard pattern using floor and mod
    vec2 st = floor(uv * 5.0);
    float check = mod(st.x + st.y, 2.0);

    // Animate the squares with time
    float pulse = sin(iTime * 0.5) * 0.5 + 0.5;

    // Create color variations for each square
    vec3 color1 = hsl(check * 0.5, 0.7, 0.6);
    vec3 color2 = hsl((check + pulse) * 0.5, 0.7, 0.6);

    // Combine colors based on checkerboard pattern
    vec3 finalColor = mix(color1, color2, check);

    return vec4(finalColor, 1.0);
}
