vec4 Effect2(vec2 uv) {
    // Center the coordinates and scale them appropriately
    vec2 st = (uv - 0.5) * 12.0;

    // Add a rotating component based on time
    float angle = iTime * 0.5;
    vec2 rotated = vec2(
        st.x * cos(angle) - st.y * sin(angle),
        st.x * sin(angle) + st.y * cos(angle)
    );

    // Create a swirling pattern using turbulence
    float swirl = turbulent(rotated * 3.0 + iTime);
    swirl += turbulent(rotated * 6.0 + iTime * 2.0);

    // Generate dynamic color transitions using HSL
    vec3 color = hsl(
        (swirl + length(st) * 0.5 + iTime) * 0.1,
        0.7,
        0.8
    );

    // Convert to RGB and output
    return vec4(hsl2rgb(color), 1.0);
}
