vec4 Effect2(vec2 uv) {
    // Center the coordinates
    vec2 st = uv - 0.5;

    // Create a time-based animation
    float time = iTime * 0.5;

    // Base color using noise for movement
    vec3 color = hsl2rgb(vec3(
        noise(st + time) * 180.0,
        0.7,
        0.5
    ));

    // Add shimmering effect with turbulence
    float shimmer = turbulent(st * 4.0 + time);
    color *= 1.0 + shimmer * 0.5;

    // Create floating orbs using multiple layers of noise
    for(float i = 0.0; i < 3.0; i++) {
        vec2 pos = st + sin(time + i) * 0.2;
        float dist = length(pos);
        float orb = smoothstep(1.0, 0.0, dist * 4.0 - i);

        // Add color variations
        color += hsl2rgb(vec3(
            noise(vec3(pos.x, pos.y, time + i)) * 180.0,
            0.5,
            0.3
        )) * orb;
    }

    // Finalize the effect with a glow
    float glow = turbulent(st * 2.0 + time);
    color += vec3(glow) * 0.4;

    return vec4(color, 1.0);
}
