vec4 Effect2(vec2 uv) {
    // Calculate distance from center
    float d = length(uv - 0.5);

    // Create expanding rings using sine wave with time offset
    float t = iTime * 0.5;
    float ring = sin(d * 8.0 + t) * 0.5 + 0.5;

    // Use HSL color space for easy hue transitions
    vec3 hslColor = vec3(
        (d * 4.0 + t) / (2.0 * PI),  // Hue: varies with distance and time
        0.8,                          // Saturation
        0.5                           // Lightness
    );

    // Convert HSL to RGB
    vec3 rgbColor = hsl2rgb(hslColor);

    // Create pulsing effect by mixing colors
    vec3 finalColor = mix(
        rgbColor * ring,          // Color when near the center
        vec3(0.0),                // Black background
        1.0 - ring               // Transition between colors and black
    );

    return vec4(finalColor, 1.0);
}
