vec4 Effect2(vec2 uv) {
    // Convert UV to polar coordinates for rotational effects
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float theta = atan2(st.y, st.x) + iTime * 0.5;

    // Add turbulence to create dynamic swirling patterns
    theta += turbulent(vec3(theta, r, iTime * 0.1)) * 0.5;
    r += turbulent(vec3(r, theta, iTime * 0.2)) * 0.2;

    // Create rotational color gradients using HSL
    vec3 color1 = hsl((theta + iTime) * 0.1, 0.7, 0.5);
    vec3 color2 = hsl((theta - iTime) * 0.1, 0.6, 0.4);

    // Combine colors with additive blending
    vec3 finalColor = (color1 + color2) * (0.5 + noise(vec2(r, theta)) * 0.5);

    return vec4(finalColor, 1.0);
}
