vec4 Effect2(vec2 uv) {
    vec2 st = uv * 5.0 + iTime; // Scale and animate UV coordinates

    // Create warping pattern using turbulence
    float warp = turbulent(st * 0.5 + iTime);
    vec2 distortedUV = uv + (warp * 0.1) * vec2(sin(iTime), cos(iTime));

    // Generate color components with noise and time
    float r = noise(vec3(distortedUV * 2.0, iTime)) * 0.5 + 0.5;
    float g = noise(vec3(distortedUV * 3.0 + vec2(1.0), iTime * 0.8)) * 0.5 + 0.5;
    float b = noise(vec3(distortedUV * 4.0 + vec2(2.0), iTime * 0.6)) * 0.5 + 0.5;

    // Mix colors with time-based hue shift
    vec3 color = hsl2rgb(vec3(
        (r + g + b) * 0.333 + iTime * 0.1, // Hue
        0.7, // Saturation
        0.5 // Lightness
    ));

    return vec4(color, 1.0);
}
