vec4 Effect2(vec2 uv) {
    // Center the UV coordinates
    vec2 centeredUV = uv - 0.5;

    // Calculate distance from center
    float dist = length(centeredUV);

    // Add pulsating effect using time
    float pulse = sin(iTime * 2.0 + dist * 4.0) * 0.3;
    float radius = dist + pulse;

    // Create circular shape with smooth edges
    float circle = smoothstep(0.5, 0.7, radius);

    // Add turbulence for dynamic effect
    vec2 noiseUV = centeredUV * 4.0 + iTime;
    float turb = turbulent(noiseUV) * 0.3;
    radius += turb;

    // Create radial gradient
    vec3 color1 = hsl2rgb(vec3(0.5, 1.0, 0.8));
    vec3 color2 = hsl2rgb(vec3(0.1, 1.0, 0.6));
    vec3 gradientColor = mix(color1, color2, radius);

    // Combine circle and gradient
    vec3 finalColor = mix(vec3(0.0), gradientColor, circle);

    return vec4(finalColor, 1.0);
}
