vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float a = atan2(st.y, st.x) + iTime * 0.5; // Add time-based rotation

    // Create hexagonal pattern by rotating and scaling coordinates
    a *= 6.0; // Hexagon has six points
    vec2 rotated = vec2(cos(a), sin(a)) * r;

    // Use noise to create texture
    float noiseVal = noise(vec3(rotated * 0.5, iTime));
    noiseVal = turbulent(vec2(rotated * 0.1 + iTime)) * 0.5 + 0.5;

    // Create pulsating color effect using HSL
    vec3 colorHSL = vec3(
        fract(iTime * 0.2) * 360.0, // Hue variation over time
        0.7, // Saturation
        0.5 + noiseVal * 0.5 // Lightness modulation with noise
    );

    // Convert HSL to RGB and mix with noise texture
    vec3 color = hsl2rgb(colorHSL);

    // Add pulsating effect based on radius
    float pulse = sin(iTime) * 0.5 + 0.5;
    color *= smoothstep(1.0, 0.0, r) * (1.0 + noiseVal * 0.5);

    return vec4(color, 1.0);
}
