vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    vec2 p = uv * 2.0 - 1.0;
    float angle = atan2(p.y, p.x);
    float radius = length(p);

    // Add rotation based on time
    angle += iTime * 0.1;

    // Convert back to cartesian coordinates
    vec2 q = vec2(
        cos(angle) * radius,
        sin(angle) * radius
    );

    // Create hexagonal grid pattern
    vec2 grid = abs(q);
    grid.x = (grid.x + grid.y) * 0.5;
    grid = floor(grid * 4.0 + 0.5);

    // Calculate color based on grid position and time
    float hue = fract(iTime / 3.0);
    vec3 color = hsl2rgb(vec3(
        (grid.x + grid.y) * 0.1 + hue,
        1.0,
        0.5 + 0.5 * sin(q.x * 4.0 + iTime)
    ));

    // Add some variation to colors
    color = mix(color, hsl2rgb(vec3(
        (grid.x - grid.y) * 0.1 + hue + 0.333,
        1.0,
        0.5 + 0.5 * sin(q.y * 4.0 + iTime)
    )), 0.5);

    return vec4(color, 1.0);
}
