vec4 Effect2(vec2 uv) {
    float time = iTime * 0.2;

    // Create rotating pattern by offsetting UV coordinates
    vec2 center = uv - 0.5;
    float angle = sin(time) + cos(time * 0.7);
    vec2 rotatedUV = vec2(
        center.x * cos(angle) - center.y * sin(angle),
        center.x * sin(angle) + center.y * cos(angle)
    ) + 0.5;

    // Create spiral pattern using polar coordinates
    vec2 toCenter = rotatedUV - 0.5;
    float radius = length(toCenter);
    float theta = atan(toCenter.y, toCenter.x);

    // Add spiral arms and noise
    float spiral = radius + sin(theta * 12.0) * 0.3;
    spiral += turbulent(vec3(rotatedUV * 4.0, time)) * 0.2;

    // Create color gradient based on distance from center
    vec3 color = hsl(
        0.7 + (radius * 0.5),   // Purple base color with variation
        1.0 - radius * 0.5,     // Saturation decreases with radius
        0.3 + spiral * 0.2      // Brightness varies with spiral pattern
    );

    // Convert HSL to RGB and add glow effect
    vec3 finalColor = hsl2rgb(color) * (1.0 - radius * 0.5);
    if (radius < 0.1) {
        finalColor *= 2.0; // Add central glow
    }

    return vec4(finalColor, 1.0);
}
