vec4 Effect2(vec2 uv) {
    // Initialize base color
    vec3 color = vec3(0.0);

    // Add multiple sine wave layers with different frequencies and phases
    for(float i = 1.0; i <= 6.0; i++) {
        // Scale UV coordinates based on frequency
        vec2 st = uv * i * 4.0;

        // Add time-based animation
        st += vec2(iTime * 0.5, iTime * 0.3);

        // Rotate the pattern slightly for each layer
        float angle = iTime * 0.1 + i * 0.2;
        st = mat2(cos(angle), sin(angle), -sin(angle), cos(angle)) * st;

        // Create sine wave patterns with varying frequencies
        float value = sin(st.x * 3.0 + iTime) * cos(st.y * 3.0 + iTime * 0.5);
        value += sin(st.x * 2.0 + iTime * 0.7) * cos(st.y * 2.0 + iTime * 0.3);

        // Add turbulence for more complex patterns
        value += turbulent(st * 4.0 + iTime) * 0.5;

        // Scale and add to the final color
        color += vec3(value * 0.1, value * 0.1, value * 0.1);
    }

    // Convert to HSL for better color control
    vec3 hslColor = vec3(
        mod(iTime * 0.2 + length(uv) * 2.0, 1.0),  // Hue
        0.7,                                      // Saturation
        0.5 + turbulent(vec3(uv * 4.0, iTime)) * 0.2  // Lightness with noise
    );

    color = hsl2rgb(hslColor) * (1.0 + sin(iTime * 0.5) * 0.5);

    return vec4(color, 1.0);
}
