vec4 Effect2(vec2 uv) {
    // Scale and animate UV coordinates
    vec2 st = uv * 5.0 + iTime;

    // Create base pattern using turbulence
    float t1 = turbulent(st);

    // Add multiple layers of noise for more detail
    float noiseSum = 0.0;
    for(float i = 0.0; i < 4.0; i++) {
        noiseSum += noise(vec2(st.x + iTime * (i+1.0), st.y) * pow(2.0, i)) / pow(2.0, i);
    }

    // Combine patterns and create color
    vec3 color = hsl(
        sin(iTime * 0.5 + t1 * 2.0) * 0.5 + 0.5,   // Hue
        0.7 + noiseSum * 0.3,                       // Saturation
        0.5 + sin(st.x * 2.0 + iTime) * 0.5         // Lightness
    );

    return vec4(hsl2rgb(color), 1.0);
}
