vec4 Effect2(vec2 uv) {
    vec3 color = vec3(0.0);

    // Create a grid of particles with noise-based movement
    for(float x = -10.0; x < 10.0; x += 1.5) {
        for(float y = -10.0; y < 10.0; y += 1.5) {
            vec2 pos = uv + vec2(x, y);

            // Add noise and turbulence to create flowing movement
            float noiseVal = noise(vec3(pos, iTime * 0.5));
            vec2 offset = vec2(turbulent(vec3(pos.x + iTime, pos.y, 0.0)), 
                              turbulent(vec3(pos.x, pos.y + iTime, 1.0))) * 0.3;

            // Calculate particle position
            vec2 finalPos = pos + offset;

            // Create color based on movement and time
            float angle = atan2(offset.y, offset.x);
            vec3 hslColor = vec3(angle / (2.0 * PI) + 0.5, 1.0, 0.8);
            vec3 rgbColor = hsl2rgb(hslColor);

            // Add glow effect
            float dist = length(uv - finalPos);
            float glow = exp(-dist * 4.0);
            color += rgbColor * glow;
        }
    }

    return vec4(color, 1.0);
}
