vec4 Effect2(vec2 uv) {
    float time = iTime * 0.5;
    vec3 color = vec3(0.0);

    // Create multiple layers of fractals
    for (int i = 0; i < 5; i++) {
        // Rotate and scale coordinates
        float angle = time + float(i) * 0.2;
        uv = abs(uv - 0.5) * 2.0;
        uv = vec2(
            uv.x * cos(angle) - uv.y * sin(angle),
            uv.x * sin(angle) + uv.y * cos(angle)
        );
        uv = pow(uv, vec2(1.0 + float(i)*0.3)) * 4.0;

        // Add noise and color
        float n = noise(vec3(uv, time));
        n += turbulent(uv * 0.5);
        n = abs(n) * 0.5 + 0.5;

        vec3 c = hsl2rgb(vec3(
            time + float(i)*0.3,
            1.0,
            n * 0.7 + 0.3
        ));

        color += c * (n * 0.4);
    }

    // Add some glow and brightness variation
    color *= abs(sin(time * 0.5)) * 0.5 + 0.5;
    color = clamp(color, 0.0, 1.0);

    return vec4(color, 1.0);
}
