vec4 Effect2(vec2 uv) {
    // Generate noise patterns at different scales
    float n1 = noise(uv * 5.0);
    float n2 = noise(uv * 10.0 + iTime);
    float n3 = turbulent(uv * 20.0 + iTime * 0.5);

    // Create a color palette using HSL
    vec3 hslColor = vec3(
        sin(iTime * 0.5) * 0.5 + 0.5,   // Hue variation over time
        1.0,                             // Saturation
        abs(n1 * 0.5 + 0.5)              // Lightness influenced by noise
    );

    // Convert to RGB and add glow effect
    vec3 color = hsl2rgb(hslColor);
    color *= exp(color * 4.0);           // Exponential glow

    // Add turbulence-based distortion
    vec2 distortedUV = uv + turbulent(uv * 10.0 + iTime) * 0.1;

    // Combine all elements with time-based animation
    return vec4(
        color * (n2 * 0.5 + 0.5), 
        exp(n3 * 2.0 + 1.0)             // Brightness modulation
    );
}
