vec4 Effect2(vec2 uv) {
    // Scale and animate UV coordinates
    vec2 st = uv * 8.0 + iTime;

    // Create sine wave patterns with RGB shifts
    vec3 color = vec3(
        sin(st.x * 0.5 + iTime) * 0.5 + 0.5,
        sin(st.y * 0.7 - iTime * 2.0) * 0.5 + 0.5,
        sin((st.x + st.y) * 0.3 + iTime * 3.0) * 0.5 + 0.5
    );

    // Add circular gradient effect
    vec2 center = vec2(0.5, 0.5);
    float dist = length(uv - center);
    float circle = 1.0 - smoothstep(0.7, 1.0, dist);

    // Incorporate noise and turbulence
    vec3 noisePattern = vec3(
        turbulent(vec3(st.x * 0.2, st.y * 0.3, iTime)),
        turbulent(vec3(st.x * 0.5, st.y * 0.4, iTime + 1.0)),
        turbulent(vec3(st.x * 0.7, st.y * 0.6, iTime + 2.0))
    );

    // Combine effects
    color = mix(color, noisePattern, 0.5);
    color *= circle;

    // Apply HSL to RGB conversion for vibrant colors
    vec3 hslColor = hsl2rgb(vec3(
        (color.r + iTime) * 0.1,
        color.g * 0.7,
        color.b * 0.8
    ));

    return vec4(hslColor, 1.0);
}
