vec4 Effect2(vec2 uv) {
    // Create a grid of orbs using noise texture sampling
    vec3 noise1 = texture(iChannel0, fract(uv * 8.0)).rgb;
    vec3 noise2 = texture(iChannel0, fract(uv * 4.0 + noise1.rg * 2.0)).rgb;

    // Calculate base color with pulsating effect
    float timeMod = mod(iTime * 0.5, 6.0);
    vec3 baseColor = hsl(timeMod * 0.17 + 0.5, 0.8, 0.6);

    // Generate pulsating wave pattern
    float wave = sin(uv.x * 2.0 + timeMod) * cos(uv.y * 2.0 + timeMod * 0.7);
    wave += sin(uv.x * 4.0 + timeMod * 1.3) * cos(uv.y * 4.0 + timeMod * 0.9);
    wave *= 0.5;

    // Create orb positions with noise-based movement
    vec2 pos = uv * 8.0;
    pos += noise1.rg * 0.1;
    pos += noise2.rg * 0.2;
    pos = fract(pos) - 0.5;

    // Calculate distance from center for fog effect
    float dist = length(pos);
    float fog = smoothstep(0.7, 1.0, dist);

    // Combine colors and apply fog
    vec3 finalColor = mix(baseColor, hsv2rgb(vec3(timeMod * 0.17 + 0.5, 0.6, 0.8)), wave * 0.5 + 0.5);
    finalColor *= 1.0 - fog * 0.5;

    return vec4(finalColor, 1.0);
}
