vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    vec2 st = uv - 0.5;
    float r = length(st);
    float a = atan2(st.y, st.x);
    // Create hexagonal pattern with pulsating edges
    r += sin(iTime) * 0.2;
    a += sin(iTime * 0.5) * 0.5;
    // Use modulo to create repeating hexagons
    float angle = mod(a + iTime, PI/3.0);

    // Create color transitions using HSL
    vec3 color = hsl(angle * 2.0 + sin(r * 10.0), 
                     0.8 - r * 0.3,
                     0.7 + cos(iTime) * 0.2);
    // Add turbulence for dynamic effect
    float turb = turbulent(vec3(uv * 10.0, iTime)) * 0.1;

    return vec4(color + turb, 1.0);
}
