vec4 Effect2(vec2 uv) {
    vec2 st = uv;

    // Create a base pattern using sine waves and noise
    float f = sin(st.x * 5.0 + iTime) * sin(st.y * 5.0 + iTime);
    f += noise(vec2(st.x * 3.0, st.y * 3.0)) * 0.5;
    f += sin(iTime * 0.2 + st.x * 4.0 + st.y * 6.0) * 0.3;

    // Create a color from the pattern
    vec3 color = hsl2rgb(vec3(
        (f * 0.5 + 0.5) * pi * 2.0,  // Hue based on pattern
        1.0,                          // Full saturation
        0.5 + sin(iTime * 0.3 + f * 4.0) * 0.2  // Varying lightness
    ));

    return vec4(color, 1.0);
}
