vec4 Effect2(vec2 uv) {
    // Create horizontal and vertical sine waves with varying frequencies
    float horizontal = sin(uv.x * 10.0 + iTime * 5.0);
    float vertical = sin(uv.y * 10.0 + iTime * 3.0);
    // Add some complexity to the waves using noise
    vec2 noiseUV = uv * 4.0;
    float noiseValue = noise(noiseUV) * 0.5;
    // Create color cycling for each wave
    vec3 horizontalColor = hsl(0.6 + iTime * 0.1, 1.0, 0.5);
    vec3 verticalColor = hsl(0.2 + iTime * 0.15, 1.0, 0.5);
    // Combine the waves with their colors
    vec3 color = vec3(0.0);
    color += horizontalColor * (abs(horizontal) * 0.5 + 0.5);
    color += verticalColor * (abs(vertical) * 0.5 + 0.5);
    // Add some turbulence for extra complexity
    float turbulence = turbulent(uv * 2.0 + iTime);
    color *= abs(turbulence * 0.5 + 0.5);
    // Mix in some noise and time-based variation
    color += noiseValue * 0.3;
    color += sin(iTime) * 0.1;
    // Apply tonal adjustments
    color = pow(color, vec3(2.2));
    return vec4(color, 1.0);
}
