vec4 Effect2(vec2 uv) {
    // Scale and animate UV coordinates
    vec2 st = uv * 5.0 + iTime;

    // Create a 3D position with time-based z component
    vec3 p = vec3(st.x, st.y, sin(iTime*0.5));

    // Generate turbulent noise values
    float n1 = turbulent(p);
    float n2 = turbulent(p * 0.5 + vec3(1.0));

    // Create color from HSL values
    vec3 hslColor = vec3(
        (n1 + iTime) * pi,       // Hue
        0.6 + abs(n2)*0.4,      // Saturation
        0.5 + n1*0.5            // Lightness
    );

    // Convert to RGB and mix with another noise layer
    vec3 color = hsl2rgb(hslColor) * (0.5 + turbulent(vec2(uv*2.0 + iTime))*0.5);

    return vec4(color, 1.0);
}
