vec4 Effect2(vec2 uv) {
    // Normalize UV coordinates to range [-1, 1]
    vec2 st = uv * 2.0 - 1.0;

    // Create a base frequency for the sine waves
    float freq = 5.0;

    // Calculate horizontal and vertical sine waves with chromatic shifts
    vec3 color1 = hsl2rgb(vec3(0.6 + sin(iTime * 0.5) * 0.2, 0.7, 0.8));
    vec3 color2 = hsl2rgb(vec3(0.2 + cos(iTime * 0.5) * 0.2, 0.7, 0.8));

    // Create horizontal and vertical sine waves with noise
    float wave1 = sin(st.x * freq + iTime) * 0.5 + 0.5;
    float wave2 = sin(st.y * freq + iTime * 0.7 + 3.14159) * 0.5 + 0.5;

    // Add noise to create turbulence effect
    vec2 noiseUV = st * 0.5 + 0.5;
    float noiseVal = noise(noiseUV);
    wave1 += noiseVal * 0.3;
    wave2 += noiseVal * 0.3;

    // Combine the waves and colors
    vec3 finalColor = color1 * wave1 + color2 * wave2;

    return vec4(finalColor, 1.0);
}
