vec4 Effect2(vec2 uv) {
    // Scale UV coordinates and add animation over time
    vec2 st = uv * 10.0 + iTime;

    // Create undulating sine wave patterns
    float wave1 = sin(st.x * 0.5 + iTime) * 0.5;
    float wave2 = cos(st.y * 0.7 - iTime) * 0.3;

    // Combine waves and add noise for texture
    float d = sin(wave1 + wave2 + st.x*0.2 + st.y*0.3);
    d += turbulent(vec2(uv * 5.0 + iTime)) * 0.4;

    // Create vibrant color shifts using HSL to RGB conversion
    vec3 color = hsl2rgb(vec3(
        mod(iTime * 0.1, PI),   // Hue that changes over time
        0.8,                   // Saturation
        0.5 + d * 0.4          // Brightness modulation by wave pattern
    ));

    return vec4(color, 1.0);
}
