vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float a = atan2(st.y, st.x) + iTime * 0.5; // Add time-based rotation

    // Create hexagonal pattern using sine waves and turbulence
    float hexPattern = sin(a * 6.0 + iTime); // Hexagonal symmetry
    hexPattern += turbulent(vec3(st * 2.0, iTime)) * 0.5; // Add turbulence

    // Pulsating color effect
    vec3 color = hsl2rgb(vec3(
        mod(iTime * 0.1 + a * 0.5, 1.0), // Hue variation over time and angle
        0.7, // Saturation
        0.5 + sin(iTime) * 0.4 // Pulsating lightness
    ));

    // Combine effects with circular mask
    float mask = smoothstep(0.9, 1.0, r);
    return vec4(color * hexPattern * mask, 1.0);
}
