vec4 Effect2(vec2 uv) {
    vec3 color = vec3(0.0);

    // Create a grid of squares with rotation
    vec2 st = fract(uv * 100.0) - 0.5;
    float speed = 0.5;
    float time = iTime * speed;

    // Rotate each square based on its position and time
    vec2 pos = vec2(
        cos(time) * st.x - sin(time) * st.y,
        sin(time) * st.x + cos(time) * st.y
    );

    // Calculate color transitions using HSL
    float hue = (pos.x + pos.y + time) * 0.5;
    vec3 hslColor = hsl(hue, 1.0, 0.5);

    // Add noise and turbulence for more interesting patterns
    float noiseScale = 0.1;
    float noiseValue = noise(vec2(pos.x * noiseScale, pos.y * noiseScale));
    float turbulentValue = turbulent(vec3(pos.x * noiseScale, pos.y * noiseScale, time));

    // Combine colors with noise and turbulence
    color += hslColor * (0.5 + 0.5 * noiseValue);
    color += vec3(0.1) * turbulentValue;

    return vec4(color, 1.0);
}
