vec4 Effect2(vec2 uv) {
    vec3 color = vec3(0.0);

    // Generate noise patterns with time variation
    float noise1 = noise(uv * 5.0 + iTime);
    float noise2 = noise(uv * 10.0 - iTime * 0.5);

    // Create moving gradient using UV coordinates and time
    vec3 grad1 = hsl(
        sin(iTime) * 0.5 + 0.5,
        0.7,
        0.5
    );

    vec3 grad2 = hsl(
        cos(iTime * 0.7) * 0.5 + 0.5,
        0.6,
        0.4
    );

    // Mix gradients based on UV position and time
    float mixAmount = sin(uv.x * pi + iTime) * 0.5 + 0.5;
    vec3 gradientMix = grad1 * (1.0 - mixAmount) + grad2 * mixAmount;

    // Add turbulence to create dynamic patterns
    vec2 turbulentUV = uv + turbulent(uv * 10.0 + iTime) * 0.1;
    float noiseMix = noise(turbulentUV * 5.0) * 0.5 + 0.5;

    // Combine everything with color transformations
    color += gradientMix * (noise1 * 0.5 + noise2 * 0.3);
    color *= hsl2rgb(vec3(
        sin(iTime * 0.3) * 0.4 + 0.6,
        0.8,
        mixAmount * 0.7 + 0.3
    ));

    return vec4(color, 1.0);
}
