vec4 Effect2(vec2 uv) {
    // Create flowing patterns with time-based movement
    vec2 st = uv * 5.0 + vec2(sin(iTime)*3.0, cos(iTime*0.7)*2.0);

    // Generate multi-scale noise patterns
    float n1 = turbulent(st * 0.5) * 0.5;
    float n2 = turbulent(st * 0.8 + 123.456) * 0.3;
    float n3 = noise(vec3(st, iTime*0.1)) * 0.2;

    // Combine patterns with color
    vec3 color = hsl(
        sin(iTime*0.3 + st.x*2.0) * 0.5 + 0.5,
        0.7 + n1 * 0.3,
        0.5 + (n1 + n2 + n3) * 0.3
    );

    // Add depth and movement to the color
    color = hsl2rgb(color);
    color *= sin(iTime*0.2 + uv.x * pi) * 0.5 + 0.5;

    return vec4(color, 1.0);
}
