vec4 Effect2(vec2 uv) {
    vec3 color = vec3(0.0);

    // Create a base noise field with time offset
    float time = iTime * 0.5;
    vec2 st = uv * 10.0 + time;

    // Generate multiple layers of orbs
    for(float i = 0.0; i < 3.0; i++) {
        // Calculate orb center with noise offset
        vec2 center = uv + noise(vec2(uv.x + time, uv.y + time)) * 0.1;
        float radius = 0.15 + abs(noise(vec3(center.x, center.y, time))) * 0.1;

        // Calculate distance from current point to orb center
        vec2 pos = uv - center;
        float dist = length(pos);

        // Create color transitions based on noise and time
        vec3 hue = hsl(time + i * 0.5, 0.7, 0.8);
        vec3 glow = mix(vec3(1.0), hue, smoothstep(radius, radius * 1.2, dist));

        // Add turbulence to create dynamic patterns
        float turb = turbulent(uv * 5.0 + time) * 0.5;
        color += glow * (1.0 - abs(dist - radius)) * (1.0 + turb);
    }

    // Final color adjustments and output
    color = clamp(color, 0.0, 1.0);
    return vec4(color, 1.0);
}
