vec4 Effect2(vec2 uv) {
    // Create a time-varying base pattern with rotating sine waves
    float time = iTime * 0.5;

    // Animate UV coordinates with time
    vec2 st = uv * 10.0 + vec2(time, time);

    // Create rotating sine wave patterns
    float sinX = sin(st.x * 0.5 + time) * 0.5 + 0.5;
    float sinY = sin(st.y * 0.5 + time + PI/2.0) * 0.5 + 0.5;

    // Combine sine waves with noise for complexity
    vec3 colorBase = vec3(sinX, sinY, 0.0);
    float noiseValue = noise(vec2(uv.x*10.0 + time, uv.y*10.0 + time));

    // Add turbulence for plasma effect
    float turb = turbulent(vec2(uv * 5.0 + time));

    // Combine all elements and convert to RGB
    vec3 finalColor = hsl2rgb(vec3(
        (colorBase.x + noiseValue) * 0.5,
        (colorBase.y + turb) * 0.7,
        0.8
    ));

    return vec4(finalColor, 1.0);
}
