vec4 Effect2(vec2 uv) {
    float time = iTime * 0.5;

    // Create three interweaving sine waves with different parameters
    vec3 amplitudes = vec3(0.1, 0.2, 0.3);
    vec3 speeds = vec3(1.0, 1.3, 1.6);
    vec3 phases = vec3(0.0, 0.5, 1.0);

    // Calculate wave positions
    vec3 waveX = sin(uv.x * 20.0 + time * speeds + phases) * amplitudes;
    vec3 waveY = cos(uv.y * 20.0 + time * speeds + phases) * amplitudes;

    // Combine waves and create interference pattern
    float combined = (waveX.y + waveY.x + waveX.z + waveY.z) * 0.5 + 0.5;

    // Add turbulence for dynamic movement
    vec2 turbulentUV = uv + turbulent(uv * 10.0 + time) * 0.1;
    combined += turbulent(turbulentUV * 5.0) * 0.3;

    // Create color shifts using HSL to RGB conversion
    vec3 baseColor = hsl2rgb(vec3(time * 0.1, 0.7, 0.8));
    vec3 shiftColor = hsl2rgb(vec3((time + uv.x * 5.0) * 0.1, 0.6, 0.7));

    // Mix colors based on wave pattern
    vec3 finalColor = mix(baseColor, shiftColor, combined);

    // Add brightness variation
    finalColor *= sin(time * 2.0 + uv.y * pi) * 0.5 + 0.5;

    return vec4(finalColor, 1.0);
}
