vec4 Effect2(vec2 uv) {
    float t = iTime * 0.5;

    // Rotating sine wave pattern
    vec2 q = uv + vec2(sin(uv.y * 3.0 + t), sin(uv.x * 3.0 + t));
    q += vec2(0.5, 0.5); // Center the coordinates

    // Create color components with time-based variation
    float r = sin(q.x * 4.0 + t) * cos(q.y * 4.0 - t);
    float g = sin(q.x * 4.0 - t) * cos(q.y * 4.0 + t);
    float b = noise(vec2(t, q.x)) * 0.5 + 0.5;

    // Convert to HSL and shift hue over time
    vec3 hslColor = vec3(
        (uv.x + uv.y + t) * 0.5,
        0.7,
        0.5
    );
    vec3 rgbColor = hsl2rgb(hslColor);

    // Combine patterns and colors
    vec3 finalColor = vec3(r, g, b) * rgbColor;

    return vec4(finalColor, 1.0);
}
