vec4 Effect2(vec2 uv) {
    // Create a time-varying base pattern
    vec2 st = uv * 5.0 + iTime;

    // Add rotation to create movement
    float angle = iTime * 0.1;
    mat2 rot = mat2(cos(angle), -sin(angle), sin(angle), cos(angle));
    st = rot * st;

    // Generate multiple layers of noise and turbulence
    float t1 = turbulent(st * 0.5);
    float t2 = turbulent((st + iTime) * 0.75);
    float t3 = turbulent((st * 0.25 + vec2(iTime)) * 0.5);

    // Combine layers with different weights
    float combined = (t1 * 0.5 + t2 * 0.3 + t3 * 0.2) * 0.5 + 0.5;

    // Create dynamic color transitions using HSL
    vec3 hslColor = vec3(
        fract(iTime * 0.1 + combined * 0.2) * pi,
        0.7 + sin(iTime * 0.5 + uv.x * pi) * 0.3,
        0.6 + cos(uv.y * pi + iTime) * 0.4
    );

    // Convert HSL to RGB and apply the combined noise pattern
    vec3 color = hsl2rgb(hslColor);
    color *= combined;

    // Add some global animation
    color += sin(iTime * 0.5 + uv.x * pi) * 0.1;

    return vec4(color, 1.0);
}
