vec4 Effect2(vec2 uv) {
    // Create time-varying wave patterns
    float t = iTime * 0.5;

    // Interweaving sine waves with RGB color transitions
    vec3 waves = vec3(
        sin(uv.x * 10.0 + t) + cos(uv.y * 10.0 + t),
        sin(uv.x * 10.0 + t + 2.0) + cos(uv.y * 10.0 + t - 2.0),
        sin(uv.x * 10.0 + t - 4.0) + cos(uv.y * 10.0 + t + 4.0)
    );

    // Add noise and turbulence for more dynamic patterns
    vec2 st = uv * 5.0;
    float n = noise(st + vec2(t, t)) * 0.5 + 0.5;
    waves += turbulent(vec3(uv, t)) * 0.4;

    // Convert to HSL and back to RGB for smooth color transitions
    vec3 hslColor = vec3(
        (waves.x + waves.y + waves.z) * 0.333,
        0.7,
        0.5 + n * 0.5
    );

    // Output the final color with alpha channel
    return vec4(hsl2rgb(hslColor), 1.0);
}
