vec4 Effect2(vec2 uv) {
    vec3 noise1 = vec3(turbulent(uv * 5.0 + iTime));
    vec3 noise2 = vec3(turbulent(uv * 10.0 - iTime));

    float time = sin(iTime * 0.5);

    vec3 color = hsl(
        (noise1.x + noise2.y) * 0.5 + 0.5,
        (noise1.y + noise2.x) * 0.5 + 0.5,
        abs(time)
    );

    float mixAmount = turbulent(uv * 3.0 + iTime);
    color = mix(color, hsl2rgb(vec3(0.0)), mixAmount);

    return vec4(color, 1.0);
}
