vec4 Effect2(vec2 uv) {
    // Scale and animate the grid
    float scale = 10.0;
    vec2 st = floor(uv * scale);

    // Add time-based rotation
    float timeRotation = sin(iTime * 0.5) * 0.5 + 0.5;
    mat2 rot = mat2(cos(timeRotation), -sin(timeRotation),
                    sin(timeRotation), cos(timeRotation));

    // Get noise value for each cell
    vec3 noiseVal = texture(iChannel0, (st + 1.0) / scale).xyz;
    float rand = noiseVal.x * 0.5 + 0.5;

    // Create rotating pattern within each cell
    vec2 pos = uv * scale - st;
    pos = rot * (pos - 0.5) + 0.5;

    // Add turbulence to position
    vec3 dist = abs(vec3(pos.x, pos.y, sin(iTime)));
    float turb = turbulent(dist * 10.0);

    // Calculate color based on distance and time
    float d = length(pos - 0.5) + turb * 0.2;
    float hue = fract(d + iTime * 0.1 + rand);

    // Convert to RGB and output
    vec3 color = hsl2rgb(vec3(hue, 0.7, 0.6));
    return vec4(color, 1.0);
}
