vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float theta = atan2(st.y, st.x);
    // Add time-based rotation
    theta += iTime * 0.5;
    // Create rotating sine wave pattern
    float sinWave = sin(theta - iTime) * 0.5 + 0.5;
    float cosWave = cos(theta + iTime) * 0.5 + 0.5;

    // Combine waves and add turbulence
    float combined = (sinWave + cosWave) * 0.5;
    combined += turbulent(vec2(r, theta)) * 0.3;
    // Create color based on amplitude
    vec3 color = hsl(
        mod(theta * 4.0 + iTime, 1.0),  // Hue
        0.7 + turbulent(vec2(r, theta + iTime)) * 0.3,  // Saturation
        combined * 0.6 + 0.4  // Lightness based on amplitude
    );
    // Add some noise to the background
    vec3 bg = hsl(
        mod(theta * 2.0 + iTime, 1.0),
        0.5,
        0.1 + noise(vec2(r, theta)) * 0.1
    );
    // Mix foreground and background based on wave amplitude
    color = mix(bg, color, combined);
    return vec4(color, 1.0);
}
