vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar coordinates
    vec2 pos = uv * 2.0 - 1.0;
    float r = length(pos);
    float theta = atan2(pos.y, pos.x);

    // Create pulsing effect based on time and radius
    float pulse = sin(iTime * 0.5 + r * 2.0) * 0.5 + 0.5;
    pulse *= sin(iTime * 0.3 + r * 4.0) * 0.5 + 0.5;

    // Create rotating pattern based on angle and time
    float rotation = theta / (pi * 2.0) + iTime * 0.1;
    vec3 color = hsl2rgb(vec3(rotation, 1.0, pulse));

    // Add turbulence effect
    float noiseFactor = turbulent(vec3(pos.x, pos.y, iTime)) * 0.5 + 0.5;
    color *= noiseFactor;

    // Create radial rays effect
    float angleOffset = theta / (pi * 2.0) * 10.0;
    float rayStrength = sin(angleOffset + iTime) * 0.5 + 0.5;
    rayStrength = pow(rayStrength, 4.0);

    // Combine effects and output
    vec3 finalColor = color * (rayStrength * pulse) * (1.0 - r * 0.2);
    return vec4(finalColor, 1.0);
}
