vec4 Effect2(vec2 uv) {
    vec3 finalColor = vec3(0.0);

    // Create hexagonal grid pattern
    vec2 st = uv * 10.0 - 5.0;
    vec2 grid = floor(st + 0.5);

    // Calculate polar coordinates for each cell
    vec2 offset = fract(st) - 0.5;
    float theta = atan(offset.y, offset.x) + sin(iTime * 0.5) * 3.14;
    float r = length(offset);

    // Create rotating hexagon pattern
    mat2 rot = mat2(cos(theta), -sin(theta), sin(theta), cos(theta));
    vec2 rotated = rot * (offset / r) * 0.5;

    // Calculate distance from center and edges
    float d1 = length(rotated + offset);
    float d2 = length(offset);

    // Create gradient borders and pulsating centers
    float border = smoothstep(0.4, 0.6, d2);
    float centerPulse = sin(iTime * 3.0 + r * 5.0) * 0.5 + 0.5;
    float inner = smoothstep(0.3, 0.7, d1);

    // Color transitions using HSL
    vec3 color1 = hsl(sin(iTime * 0.2 + grid.x * 0.1) * 0.6 + 0.2, 0.8, 0.5);
    vec3 color2 = hsl(sin(iTime * 0.2 + grid.y * 0.1) * 0.6 + 0.7, 0.7, 0.4);

    // Combine elements
    finalColor += border * color2;
    finalColor += inner * centerPulse * color1;

    // Add noise for extra texture
    float noiseVal = noise(vec3(uv * 5.0, iTime)) * 0.1;
    finalColor *= 1.0 + noiseVal;

    return vec4(finalColor, 1.0);
}
